import React, { useRef } from 'react';
import HelloWorld from '../components/HelloWorld'

class MainView extends React.Component {
  state = {
    skipHello: false,
  };
  constructor() {
    super();
    this.postHello = React.createRef();
  }



  handleClick = e => {
    this.state.skipHello = true;
    console.log('click ', this.state.skipHello);
    // this.setState({ current: e.key });

  };

  scrollHelloDown = () => {
    this.setState({mobileOpen: !this.state.mobileOpen});
  }

  render() {
    console.log(this.state)
    if (this.state.skipHello == true) {
      window.scrollTo({
        top: this.postHello.current.offsetTop,
        behavior: "smooth"
      });
    }
    return (
        <div>
            <HelloWorld scrollDown={this.handleClick}/>
            {
              this.state.skipHello ?

              <div className="helloWorldCont" style={{backgroundColor: 'white'}} ref={this.postHello}>

            </div>
            :
            <div></div>

            }

            {this.props.children}
        </div>
    );
  }
}

export default MainView;
